from etherscan.accounts import Account
import json


with open('private.json', 'r') as key_file:
    etherScanKey= json.loads(key_file.read())['etherscanKey']



TX_HASH = '0x348FC118bcC65a92dC033A951aF153d14D945312'
api = Account(address=TX_HASH,api_key=etherScanKey)

text = api.get_transaction_page(page=1,offset=10000,sort='des')
print(text)